import React, { createContext, useState } from "react";
import Allfotos from "../fotos.json"
export const GaleryContext = createContext();

export default function GaleryProvider({ children }) {

  const data= Allfotos.photos;

  const [fotos, setFotos] = useState(data);

  return (
    <GaleryContext.Provider
      value={{
        fotos,
        setFotos
      }}
    >
      {children}
    </GaleryContext.Provider>
  );
}
